CREATE TABLE IF NOT EXISTS gossip_menus(
  id INTEGER NOT NULL,
  text_id INTEGER NOT NULL,
  PRIMARY KEY (id, text_id),
  FOREIGN KEY (text_id) REFERENCES npc_texts(id)
);

CREATE TABLE IF NOT EXISTS gossip_menu_options(
  menu_id INTEGER NOT NULL DEFAULT '0',
  id INTEGER NOT NULL DEFAULT '0',
  option_icon INTEGER NOT NULL DEFAULT '0',
  option_text TEXT DEFAULT NULL,
  option_id INTEGER NOT NULL DEFAULT '0',
  npc_option_npcflag INTEGER NOT NULL DEFAULT '0',
  action_menu_id INTEGER NOT NULL DEFAULT '0',
  action_poi_id INTEGER NOT NULL DEFAULT '0',
  box_coded INTEGER NOT NULL DEFAULT '0',
  box_money INTEGER NOT NULL DEFAULT '0',
  box_text TEXT DEFAULT NULL,
  PRIMARY KEY (menu_id, id)
);

DELETE FROM gossip_menus;
DELETE FROM gossip_menu_options;


